#Requires AutoHotkey v2.0

Esc:: {
    if WinExist("PowerScribe 360 | Reporting") {
        ControlSend("{F12}", , "PowerScribe 360 | Reporting")
    } else {
        Send("{Esc}")
    }
}

#Requires AutoHotkey v2.0

`:: {
    if WinExist("PowerScribe 360 | Reporting") {
        ControlSend("{F4}", , "PowerScribe 360 | Reporting")
    } else {
        Send("`")
    }
}

$Tab:: {
    if WinExist("PowerScribe 360 | Reporting") {
        ControlSend("{Tab}", , "PowerScribe 360 | Reporting")
    } else {
        Send("{Tab}")
    }
}

#Requires AutoHotkey v2.0

$+Tab:: {
    if WinExist("PowerScribe 360 | Reporting") {
        ControlSend("+{Tab}", , "PowerScribe 360 | Reporting")
    } else {
        Send("+{Tab}")
    }
}

#Requires AutoHotkey v2.0

CapsLock:: {
    if WinExist("PowerScribe 360 | Reporting") {
        ControlSend("^+1", , "PowerScribe 360 | Reporting")
    } else {
        Send("{CapsLock}")
    }
}
